// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Banner from './components/Banner';
import Dashboard from './components/Dashboard';

function App() {
  const [bannerVisible, setBannerVisible] = useState(true);
  const [bannerProps, setBannerProps] = useState({
    description: 'This is a banner.',
    initialTimer: 10,
    link: ''
  });

  return (
    <Router>
      <div className="App">
        {bannerVisible && <Banner {...bannerProps} />}
        <Routes>
          <Route path="/dashboard" element={<Dashboard setBannerVisible={setBannerVisible} setBannerProps={setBannerProps} />} />
          <Route path="/" element={<h1>Welcome to the Dynamic One-Page Website</h1>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
