// src/components/Dashboard.js
import React, { useState } from 'react';
import axios from 'axios';

function Dashboard({ setBannerVisible, setBannerProps }) {
  const [description, setDescription] = useState('');
  const [timer, setTimer] = useState(10);
  const [link, setLink] = useState('');

  const handleSave = async () => {
    await axios.post('http://localhost:5000/api/banner', { description, timer, link });
    setBannerProps({ description, initialTimer: timer, link });
    setBannerVisible(true);
  };

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>
      <label>
        Banner Description:
        <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
      </label>
      <label>
        Banner Timer (seconds):
        <input type="number" value={timer} onChange={(e) => setTimer(e.target.value)} />
      </label>
      <label>
        Banner Link:
        <input type="text" value={link} onChange={(e) => setLink(e.target.value)} />
      </label>
      <button onClick={handleSave}>Save</button>
      <button onClick={() => setBannerVisible(false)}>Hide Banner</button>
    </div>
  );
}

export default Dashboard;
